package services

import repositories._
import models._
import com.google.inject.Inject
import java.util.regex.Pattern

trait SubscriptionService {
  def save(user: User): Boolean

  def getUser(id: String): User

  def getSurvey(id: String): Survey

  def save(userId: String, survey: Survey): Boolean

  def listSurveys(): Seq[Survey]

}

/**
 * @author Henrique
 */

class SubscriptionServiceDefault @Inject() (userRepository: UserRepository, surveyRepository: SurveyRepository) extends SubscriptionService {

  def getUser(id: String): User = {
    return userRepository.getById(id)
  }

  def getSurvey(id: String): Survey = {
    val user = userRepository.getById(id)

    if (user == null)
      return null
    else
      return surveyRepository.getByUser(id)
  }

  def listSurveys(): Seq[Survey] = {
    surveyRepository.listAll()
  }

  def save(user: User): Boolean = {
    if (user.name == null || !isValidEmail(user.email))
      return false;

    return userRepository.insert(user)
  }
  def save(userId: String, survey: Survey): Boolean = {
    val user = userRepository.getById(userId)
    if (user == null)
      return false;
    else
      return surveyRepository.insert(userId, survey)
  }

